mapper.update:

update DayPlan set 
dContents = #{dContents}, startTime = to_date(#{startTime},'yyyy/mm/dd hh24:mi'), 
endTime = to_date(#{endTime},'yyyy/mm/dd hh24:mi') WHERE dayPlanNum = #{dayPlanNum}

mapper.select:

select count(bRecordNum) from btmRecord
	where to_date(to_char(sysdate,'yyyy/mm/dd'),'yyyy/mm/dd') >= to_date(to_char(startDate,'yyyy/mm/dd'),'yyyy/mm/dd')
	and to_date(to_char(sysdate,'yyyy/mm/dd'),'yyyy/mm/dd') <= to_date(to_char(endDate,'yyyy/mm/dd'),'yyyy/mm/dd') and memberId = #{userId}



select bRecordNum from btmRecord 
where to_date(#{startDate},'yyyy/mm/dd') >= to_date(to_char(startDate,'yyyy/mm/dd'),'yyyy/mm/dd') and 
to_date(#{startDate},'yyyy/mm/dd') <= to_date(to_char(endDate,'yyyy/mm/dd'),'yyyy/mm/dd') and memberId = #{userId}
